create table exp_krit_str
(
    id     numeric not null
        constraint exp_krit_str_pk
            primary key,
    zag    varchar(10),
    nom    varchar(10),
    krit   varchar(2000),
    vid    varchar(600),
    mkb    varchar(200),
    typ    varchar(200),
    vz     varchar(5),
    st1    varchar(20),
    id_zag numeric
        constraint exp_krit_str_fk
            references exp_krit_zag
);

alter table exp_krit_str
    owner to exp_user;

create unique index exp_krit_str_id_uindex
    on exp_krit_str (id);

