create table exp_mkb10
(
    id         integer     not null,
    kodmkb     varchar(10) not null
        constraint exp_mkb10_pk
            primary key,
    name_sh    varchar(100),
    name_mkb10 varchar(250),
    enable     integer
);

alter table exp_mkb10
    owner to exp_user;

create unique index exp_mkb10_id_uindex
    on exp_mkb10 (id);

create unique index exp_mkb10_kodmkb_uindex
    on exp_mkb10 (kodmkb);

