create view exp_view_usl(id, usl_code, name, rel, enable) as
SELECT exp_usl.id,
       exp_usl.usl_code,
       exp_usl.name,
       exp_usl.rel,
       exp_usl.enable
FROM exp_usl
WHERE (EXISTS(SELECT 1
              FROM exp_std_usl
              WHERE exp_std_usl.kod_usl::text = exp_usl.usl_code::text));

alter table exp_view_usl
    owner to exp_user;

