create view exp_view_usl_check(id, vid, check_all) as
SELECT exp_sluch.id,
       exp_krit_str.vid,
       exp_result.status AS check_all
FROM exp_sluch
         LEFT JOIN exp_result ON exp_result.id_sluch = exp_sluch.id
         LEFT JOIN exp_krit_str ON exp_krit_str.id = exp_result.id_krit_str::numeric;

alter table exp_view_usl_check
    owner to exp_user;

